mapper.select:
select * from tb_article


        and category_id = #{categoryId}


        and state = #{state}

    and create_user_id = #{userId}

order by create_time desc

select a.*, COALESCE(s.view_count, 0) as view_count, COALESCE(s.like_count, 0) as like_count from tb_article a left join tb_article_stats s on a.id = s.article_id
and (a.title like concat('%', #{keyword}, '%') or a.content like concat('%', #{keyword}, '%'))
and a.category_id = #{categoryId}
and a.state = #{state}
order by a.create_time desc
